params.spec:
{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "data": [
    {
      "name": "credits",
      "url": {
        "index": "metrics-ess_billing.credits-*",
        "body": {
          "size": 0,
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%"
              ],
              "must_not": [
                "%dashboard_context-must_not_clause%"],
              "filter": [
                "%dashboard_context-filter_clause%",
                {
                  "range": {
                    "@timestamp": {
                      "gte": "now-24h"
                    }
                  }
                },
                {
                  "term": {
                    "ess.billing.active": true
                  }
                },
                {
                  "range": {
                    "ess.billing.ecu_balance": {
                      "gt": 0
                    }
                  }
                }
              ]
            }
          },
          "aggs": {
            "ecu_balance": {
              "sum": {
                "field": "ess.billing.ecu_balance"
              }
            },
            "ecu_quantity": {
              "sum": {
                "field": "ess.billing.ecu_quantity"
              }
            },
            "start": {
              "min": {
                "field": "ess.billing.start"
              }
            },
            "end": {
              "max": {
                "field": "ess.billing.end"
              }
            },
            "@timestamp": {
              "max": {
                "field": "@timestamp"
              }
            }
          }
        }
      }
      "format": {"property": "aggregations"},
      "transform": [
        {
          "type": "project",
          "fields": [
            "ecu_balance.value",
            "ecu_quantity.value",
            "start.value_as_string",
            "end.value_as_string",
            "@timestamp.value_as_string"
          ],
          "as": [
            "ecu_balance",
            "ecu_quantity",
            "start",
            "end",
            "timestamp"
          ]
        },
        {
          "type": "formula",
          "expr": "utcParse(datum.start, '%Y-%m-%dT%H:%M:%S.%LZ')",
          "as": "start"
        },
        {
          "type": "formula",
          "expr": "utcParse(datum.end, '%Y-%m-%dT%H:%M:%S.%LZ')",
          "as": "end"
        },
        {
          "type": "formula",
          "expr": "utcParse(datum.timestamp, '%Y-%m-%dT%H:%M:%S.%LZ')",
          "as": "timestamp"
        }
      ]
    },
    {
      "name": "billing_data",
      "url": {
        "%context%": true,
        "%timefield%": "@timestamp",
        "index": "metrics-ess_billing.billing-*",
        "body": {
          "size": 0,
          "aggs": {
            "daily_breakdown": {
              "date_histogram": {
                "min_doc_count": 1,
                "field": "@timestamp",
                "calendar_interval": "day",
                "order": {"_key": "desc"},
                "extended_bounds": {
                  "min": {"%timefilter%": "min"},
                  "max": {"%timefilter%": "max"}
                }
              },
              "aggs": {"credit_use": {"sum": {"field": "ess.billing.total_ecu"}}}
            }
          }
        }
      },
      "format": {"property": "aggregations.daily_breakdown.buckets"},
      "transform": [
        {
          "type": "project",
          "fields": [
            "key_as_string",
            "credit_use.value"
          ],
          "as": [
            "timestamp",
            "credit_use"
          ]
        },
        {
          "type": "formula",
          "expr": "utcParse(datum.timestamp, '%Y-%m-%dT%H:%M:%S.%LZ')",
          "as": "timestamp"
        },
        {
          "type": "formula",
          "expr": "data('credits')[0]",
          "as": "credits"
        },
        {
          "type": "project",
          "fields": [
            "credits.timestamp",
            "credits.start",
            "credits.end",
            "credits.ecu_balance",
            "credits.ecu_quantity",
            "timestamp",
            "credit_use"
          ],
          "as": [
            "credit_measure_timestamp",
            "credit_start",
            "credit_end",
            "credit_balance",
            "credit_quantity",
            "timestamp",
            "credit_use"
          ]
        }
      ]
    },
    {
      "name": "billing_pre_credits",
      "source": "billing_data",
      "transform": [
        {
          "type": "filter",
          "expr": "datum.timestamp < datum.credit_measure_timestamp"
        },
        {
          "type": "window",
          "sort": {"field": "timestamp", "order": "descending"},
          "fields":["credit_use"],
          "ops": ["sum"],
          "as": ["cumulative_credit_use"]
        },
        {
          "type": "formula",
          "expr": "datum.cumulative_credit_use + datum.credit_balance",
          "as": "ecu_balance"
        }
      ]
    },
    {
      "name": "billing_post_credits",
      "source": "billing_data",
      "transform": [
        {
          "type": "filter",
          "expr": "datum.timestamp >= datum.credit_measure_timestamp"
        },
        {
          "type": "formula",
          "expr": "datum.timestamp - datum.credit_measure_timestamp == 0 ? 0 : datum.credit_use",
          "as": "credit_use"
        },
        {
          "type": "window",
          "sort": {"field": "timestamp", "order": "ascending"},
          "fields":["credit_use"],
          "ops": ["sum"],
          "as": ["cumulative_credit_use"]
        },
        {
          "type": "formula",
          "expr": "datum.credit_balance - datum.cumulative_credit_use",
          "as": "ecu_balance"
        }
      ]
    },
    {
      "name": "historical_billing",
      "source": ["billing_pre_credits", "billing_post_credits"],
      "transform": [
        {
          "type": "project",
          "fields": [
            "timestamp",
            "ecu_balance"
          ],
          "as":[
            "day",
            "ecu_balance"
          ]
        },
        {
          "type": "formula",
          "expr": "'Historical consumption'",
          "as": "type"
        },
        {
          "type": "formula",
          "expr": "utcFormat(datum.day, '%Y-%m-%d')",
          "as": "key"
        },
        {
          "type": "collect",
          "sort": {"field": "day", "order": "descending"}
        }
      ]
    },
    {
      "name": "expected_projection",
      "url": {
        "index": "metrics-ess_billing.billing-*",
        "body": {
          "size": 0,
          "query": {
            "bool": {
              "must": [
                "%dashboard_context-must_clause%"
              ],
              "must_not": [
                "%dashboard_context-must_not_clause%"
              ],
              "filter": [
                "%dashboard_context-filter_clause%",
                {
                  "range": {
                    "@timestamp": {
                      "gte": "now-3d/d",
                      "lt": "now-1d/d"
                    }
                  }
                }
              ]
            }
          },
          "aggs": {
            "per_day": {
              "date_histogram": {
                "field": "@timestamp",
                "calendar_interval": "day"
              },
              "aggs": {
                "cost_per_day": {
                  "sum": {
                    "field": "ess.billing.total_ecu"
                  }
                }
              }
            },
            "avg_cost_per_day": {
              "avg_bucket": {
                "buckets_path": "per_day>cost_per_day"
              }
            }
          }
        }
      },
      "format": {"property": "aggregations.avg_cost_per_day.value"},
      "transform": [
        {
          "type": "formula",
          "expr": "data('historical_billing')[0]",
          "as": "last_state"
        },
        {
          "type": "formula",
          "expr": "data('credits')[0]",
          "as": "credits"
        },
        {
          "type": "project",
          "fields": [
            "last_state.day",
            "last_state.ecu_balance",
            "credits.end",
            "data"
          ],
          "as": [
            "last_timestamp",
            "ecu_balance",
            "end",
            "quantity"
          ]
        },
        {
          "type": "formula",
          "expr": "timeOffset('day', datum.last_timestamp)",
          "as": "last_timestamp"
        },
        {
          "type": "formula",
          "expr": "utcSequence('day', datum.last_timestamp, datum.end)",
          "as": "day"
        },
        {
          "type": "flatten",
          "fields": ["day"]
        },
        {
          "type": "window",
          "fields":["quantity"],
          "ops": ["sum"],
          "as": ["cumulative_quantity"]
        },
        {
          "type": "formula",
          "expr": "datum.ecu_balance-datum.cumulative_quantity",
          "as": "ecu_balance"
        },
        {
          "type": "project",
          "fields": ["day", "ecu_balance"]
        },
        {
          "type": "filter",
          "expr": "datum.ecu_balance > 0"
        },
        {
          "type": "formula",
          "expr": "'Expected consumption'",
          "as": "type"
        },
        {
          "type": "formula",
          "expr": "utcFormat(datum.day, '%Y-%m-%d')",
          "as": "key"
        }
      ]
    },
    {
      "name": "contractual_projection",
      "values": [{}],
      "transform": [
        {
          "type": "formula",
          "expr": "data('historical_billing')[0]",
          "as": "last_state"
        },
        {
          "type": "formula",
          "expr": "data('credits')[0]",
          "as": "credits"
        },
        {
          "type": "project",
          "fields": [
            "last_state.day",
            "last_state.ecu_balance",
            "credits.end"
          ],
          "as": [
            "last_timestamp",
            "ecu_balance",
            "end"
          ]
        },
        {
          "type": "formula",
          "expr": "datum.ecu_balance/((datum.end-datum.last_timestamp)/1000/3600/24)",
          "as": "quantity"
        },
        {
          "type": "formula",
          "expr": "timeOffset('day', datum.last_timestamp)",
          "as": "last_timestamp"
        },
        {
          "type": "formula",
          "expr": "utcSequence('day', datum.last_timestamp, datum.end)",
          "as": "day"
        },
        {
          "type": "flatten",
          "fields": ["day"]
        },
        {
          "type": "window",
          "fields":["quantity"],
          "ops": ["sum"],
          "as": ["cumulative_quantity"]
        },
        {
          "type": "formula",
          "expr": "datum.ecu_balance-datum.cumulative_quantity",
          "as": "ecu_balance"
        },
        {
          "type": "project",
          "fields": ["day", "ecu_balance"]
        },
        {
          "type": "formula",
          "expr": "'Contractual target'",
          "as": "type"
        },
        {
          "type": "formula",
          "expr": "utcFormat(datum.day, '%Y-%m-%d')",
          "as": "key"
        }
      ]
    },
    {
      "name": "aggregated_billing",
      "source": ["historical_billing", "expected_projection", "contractual_projection"],
      "transform": [
        {
          "type": "collect",
           "sort": {"field": "day", "order": "descending"}
        }
      ]
    }
  ],
  "scales": [
    {
      "name": "x",
      "type": "time",
      "range": "width",
      "domain": {"data": "aggregated_billing", "field": "day"}
    },
    {
      "name": "y",
      "type": "linear",
      "range": "height",
      "nice": true,
      "zero": false,
      "domain": {"data": "aggregated_billing", "field": "ecu_balance"}
    },
    {
      "name": "color",
      "type": "ordinal",
      "range": "category",
      "domain": {"data": "aggregated_billing", "field": "type"}
    }
  ],
  "axes": [
    {"orient": "bottom", "scale": "x"},
    {"orient": "left", "scale": "y"}
  ],
  "signals": [
    {
      "name": "day",
      "update": "now()",
      "on": [
        {
          "events": "pointermove",
          "update": "invert('x', clamp(x(), 0, width))"
        }
      ]
    },
    {
      "name": "historical_balance",
      "update": "pluck(data('historical_billing'), 'ecu_balance')[indexof(pluck(data('historical_billing'), 'key'), utcFormat(now(), '%Y-%m-%d'))]",
      "on": [
        {
          "events": "pointermove",
          "update": "pluck(data('historical_billing'), 'ecu_balance')[indexof(pluck(data('historical_billing'), 'key'), utcFormat(invert('x', clamp(x(), 0, width)), '%Y-%m-%d'))]"
        }
      ]
    },
    {
      "name": "contractual_balance",
      "update": "pluck(data('contractual_projection'), 'ecu_balance')[indexof(pluck(data('contractual_projection'), 'key'), utcFormat(now(), '%Y-%m-%d'))]",
      "on": [
        {
          "events": "pointermove",
          "update": "pluck(data('contractual_projection'), 'ecu_balance')[indexof(pluck(data('contractual_projection'), 'key'), utcFormat(invert('x', clamp(x(), 0, width)), '%Y-%m-%d'))]"
        }
      ]
    },
    {
      "name": "expected_balance",
      "update": "pluck(data('expected_projection'), 'ecu_balance')[indexof(pluck(data('expected_projection'), 'key'), utcFormat(now(), '%Y-%m-%d'))]",
      "on": [
        {
          "events": "pointermove",
          "update": "pluck(data('expected_projection'), 'ecu_balance')[indexof(pluck(data('expected_projection'), 'key'), utcFormat(invert('x', clamp(x(), 0, width)), '%Y-%m-%d'))]"
        }
      ]
    }
  ],
  "marks": [
    {
      "type": "group",
      "from": {
        "facet": {
          "name": "series",
          "data": "aggregated_billing",
          "groupby": "type"
        }
      },
      "marks": [
        {
          "type": "line",
          "from": {"data": "series"},
          "encode": {
            "update": {
              "x": {"scale": "x", "field": "day"},
              "y": {"scale": "y", "field": "ecu_balance"},
              "stroke": {"scale": "color", "field": "type"},
              "strokeWidth": {"value": 2}
            }
          }
        }
      ]
    },
    {
      "type": "rule",
      "encode": {
        "update": {
          "x": {"scale": "x", "signal": "day", "offset": 0.5},
          "y": {"scale": "y", "signal": "extent([historical_balance, contractual_balance, expected_balance])[1]", "offset": 0.5},
          "y2": {"field": {"group": "height"}},
          "stroke": {"value": "black"}
        }
      }
    },
    {
      "type": "rule",
      "encode": {
        "update": {
          "x": {"value": 0},
          "x2": {"scale": "x", "signal": "day", "offset": 0.5},
          "y": {"scale": "y", "signal": "historical_balance", "offset": 0.5},
          "stroke": {"scale": "color", "signal": "'Historical consumption'"},
          "opacity": { "signal": "historical_balance == null ? 0 : 1"}
        }
      }
    },
    {
      "type": "rule",
      "encode": {
        "update": {
          "x": {"value": 0},
          "x2": {"scale": "x", "signal": "day", "offset": 0.5},
          "y": {"scale": "y", "signal": "contractual_balance", "offset": 0.5},
          "stroke": {"scale": "color", "signal": "'Contractual target'"},
          "opacity": { "signal": "contractual_balance == null ? 0 : 1"}
        }
      }
    },
    {
      "type": "rule",
      "encode": {
        "update": {
          "x": {"value": 0},
          "x2": {"scale": "x", "signal": "day", "offset": 0.5},
          "y": {"scale": "y", "signal": "expected_balance", "offset": 0.5},
          "stroke": {"scale": "color", "signal": "'Expected consumption'"},
          "opacity": { "signal": "expected_balance == null ? 0 : 1"}
        }
      }
    },
    {
      "type": "text",
      "encode": {
        "update": {
          "x": {"scale": "x", "signal": "day"},
          "y2": {"field": {"group": "height"}, "offset": 30},
          "align": {"value": "center"},
          "text": {"signal": "timeFormat(day, '%d %b %Y')"},
          "fill": {"value": "black"}
        }
      }
    },
    {
      "type": "text",
      "encode": {
        "update": {
          "x": {"offset": -30},
          "y": {"scale": "y", "signal": "historical_balance", "offset": 0.5},
          "align": {"value": "center"},
          "text": {"signal": "format(ceil(historical_balance), ',.0f')"},
          "fill": {"scale": "color", "signal": "'Historical consumption'"},
          "opacity": { "signal": "historical_balance == null ? 0 : 1"}
        }
      }
    },
    {
      "type": "text",
      "encode": {
        "update": {
          "x": {"offset": -30},
          "y": {"scale": "y", "signal": "contractual_balance", "offset": 0.5},
          "align": {"value": "center"},
          "text": {"signal": "format(ceil(contractual_balance), ',.0f')"},
          "fill": {"scale": "color", "signal": "'Contractual target'"},
          "opacity": { "signal": "contractual_balance == null ? 0 : 1"}
        }
      }
    },
    {
      "type": "text",
      "encode": {
        "update": {
          "x": {"offset": -30},
          "y": {"scale": "y", "signal": "expected_balance", "offset": 0.5},
          "align": {"value": "center"},
          "text": {"signal": "format(ceil(expected_balance), ',.0f')"},
          "fill": {"scale": "color", "signal": "'Expected consumption'"},
          "opacity": { "signal": "expected_balance == null ? 0 : 1"}
        }
      }
    }
  ],
  "legends": [
    {
      "fill": "color",
      "orient": "top-right",
      "encode": {
        "title": {"update": {"fontSize": {"value": 14}}},
        "labels": {
          "enter": {"fontSize": {"value": 12}, "fill": {"value": "black"}}
        },
        "symbols": {"update": {"stroke": {"value": "transparent"}}}
      }
    }
  ]
}
