// Alert triggers when the memory_grants_pending exceeds the recommended threshold value {5} within the look back time window.
// The alert is grouped by Server Name.
// You can adjust the threshold value by modifying the memory_grants_pending in the WHERE clause.
FROM metrics-microsoft_sqlserver.performance-default
| KEEP mssql.metrics.memory_grants_pending, mssql.metrics.server_name, @timestamp
| WHERE mssql.metrics.memory_grants_pending IS NOT NULL
| STATS memory_grants_pending = MAX(mssql.metrics.memory_grants_pending) BY mssql.metrics.server_name
| WHERE memory_grants_pending > 5
| SORT memory_grants_pending DESC
