// Alert triggers when the used_log_space_pct exceeds the recommended threshold value {90%} within the look back time window.
// The alert is grouped by Server Name.
// You can adjust the threshold value by modifying the used_log_space in the WHERE clause.
FROM metrics-microsoft_sqlserver.transaction_log-default
| KEEP mssql.metrics.used_log_space_pct, mssql.metrics.server_name, @timestamp
| WHERE mssql.metrics.used_log_space_pct IS NOT NULL
| STATS used_log_space = MAX(mssql.metrics.used_log_space_pct) * 100 BY mssql.metrics.server_name
| WHERE used_log_space > 90
| EVAL used_log_space = ROUND(used_log_space, 2)
| SORT used_log_space DESC
