esqlQuery.esql:
// Alert when maximum replication lag exceeds 10 seconds for any replica set.
// Aggregates per data_stream.namespace and replica set name.
FROM metrics-mongodb.replstatus-*
| STATS replication_lag=MAX(mongodb.replstatus.lag.max) BY data_stream.namespace, mongodb.replstatus.set_name
| WHERE replication_lag > 10000
